import React, { useState, useEffect } from 'react';
import { Send } from 'lucide-react';
import axios from 'axios';
import {
  AppContainer,
  ChatContainer,
  InputContainer,
  Input,
  SendButton,
  MessageContainer,
  MessageBubble,
  Avatar,
  WeatherIcon,
  WeatherCard,
  WeatherHeader,
  WeatherLocation,
  WeatherDate,
  WeatherInfo,
  WeatherTemp,
  WeatherDescription,
  WeatherDetails,
  WeatherDetail,
  DetailLabel,
  HumidityIcon,
  WindIcon,
  TempIcon,
  RainIcon
} from './components/UI';

// Define API key directly - this is more reliable for development
// For production, you would use a backend proxy instead
const WEATHER_API_KEY = "a0548a4003f819e3be7395afec705704";

const App = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  
  // Sample suggestions for the welcome screen
  const suggestions = [
    { id: 1, text: "What's the weather in London?", icon: "thermometer" },
    { id: 2, text: "Will it rain tomorrow in New York?", icon: "rainy" },
    { id: 3, text: "Weather forecast for Paris", icon: "thermometer" },
    { id: 4, text: "Temperature in Tokyo today", icon: "thermometer" }
  ];

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const fetchWeatherData = async (query) => {
    setLoading(true);
    try {
      // Add user message
      setMessages(prev => [...prev, { text: query, isUser: true }]);
      
      // Initial response
      setMessages(prev => [...prev, { 
        text: `Checking weather information for "${query}"...`, 
        isUser: false,
        isLoading: true
      }]);
      
      // Extract city name from query (this is a simple extraction, you might want to use NLP for better results)
      const cityMatch = query.match(/weather\s+in\s+([\w\s]+)|([\w\s]+)\s+weather|forecast\s+for\s+([\w\s]+)|temperature\s+in\s+([\w\s]+)/i);
      let city = "London"; // Default city
      
      if (cityMatch) {
        // Find the first non-undefined group
        city = cityMatch.slice(1).find(match => match !== undefined)?.trim() || city;
      }
      
      // API call to OpenWeatherMap using the directly defined API key
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${WEATHER_API_KEY}&units=metric`
      );
      
      const weatherData = response.data;
      
      // Format the response
      const weatherResponse = {
        location: weatherData.name,
        country: weatherData.sys.country,
        temperature: Math.round(weatherData.main.temp),
        description: weatherData.weather[0].description,
        icon: mapWeatherIcon(weatherData.weather[0].main),
        humidity: weatherData.main.humidity,
        wind: weatherData.wind.speed,
        feelsLike: Math.round(weatherData.main.feels_like),
        date: new Date().toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' })
      };
      
      // Update the last message with the weather data
      setMessages(prev => {
        const newMessages = [...prev];
        // Remove the loading message
        newMessages.pop();
        // Add the weather card message
        newMessages.push({
          text: `Here's the current weather for ${weatherResponse.location}:`,
          isUser: false,
          weatherData: weatherResponse
        });
        return newMessages;
      });
    } catch (error) {
      console.error('Error fetching weather data:', error);
      // Update the last message with the error
      setMessages(prev => {
        const newMessages = [...prev];
        // Remove the loading message
        newMessages.pop();
        // Add the error message
        newMessages.push({
          text: `Sorry, I couldn't find weather information for that location. Please try another city.`,
          isUser: false
        });
        return newMessages;
      });
    } finally {
      setLoading(false);
      setInput('');
    }
  };

  // Map weather conditions to icon types
  const mapWeatherIcon = (condition) => {
    const conditionMap = {
      'Clear': 'sunny',
      'Clouds': 'cloudy',
      'Rain': 'rainy',
      'Drizzle': 'rainy',
      'Thunderstorm': 'rainy',
      'Snow': 'cloudy',
      'Mist': 'cloudy',
      'Fog': 'cloudy',
      'Haze': 'cloudy'
    };
    
    return conditionMap[condition] || 'sunny';
  };

  const handleSendMessage = () => {
    if (input.trim() === '' || loading) return;
    fetchWeatherData(input);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    } else if (e.key === 'Enter' && e.shiftKey) {
      // Allow line breaks with Shift+Enter
    }
  };

  const handleSuggestionClick = (suggestion) => {
    if (loading) return;
    fetchWeatherData(suggestion.text);
  };

  // Render a weather card
  const renderWeatherCard = (weatherData) => (
    <WeatherCard>
      <WeatherHeader>
        <WeatherLocation>{weatherData.location}, {weatherData.country}</WeatherLocation>
        <WeatherDate>{weatherData.date}</WeatherDate>
      </WeatherHeader>
      <WeatherInfo>
        <div>
          <WeatherIcon type={weatherData.icon} size={48} />
        </div>
        <div>
          <WeatherTemp>{weatherData.temperature}°C</WeatherTemp>
          <WeatherDescription>{weatherData.description}</WeatherDescription>
        </div>
      </WeatherInfo>
      <WeatherDetails>
        <WeatherDetail>
          <TempIcon />
          <div>{weatherData.feelsLike}°C</div>
          <DetailLabel>Feels Like</DetailLabel>
        </WeatherDetail>
        <WeatherDetail>
          <HumidityIcon />
          <div>{weatherData.humidity}%</div>
          <DetailLabel>Humidity</DetailLabel>
        </WeatherDetail>
        <WeatherDetail>
          <WindIcon />
          <div>{weatherData.wind} m/s</div>
          <DetailLabel>Wind</DetailLabel>
        </WeatherDetail>
      </WeatherDetails>
    </WeatherCard>
  );

  return (
    <AppContainer>
      <header className="p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button className="mr-4">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <line x1="3" y1="12" x2="21" y2="12"></line>
              <line x1="3" y1="6" x2="21" y2="6"></line>
              <line x1="3" y1="18" x2="21" y2="18"></line>
            </svg>
          </button>
          <div>
            <h1 className="text-xl font-bold">Weather Chat</h1>
            <p className="text-sm text-gray-500">Ask me anything about weather worldwide</p>
          </div>
        </div>
      </header>
      
      <ChatContainer>
        {messages.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full">
            <div className="bg-indigo-500 rounded-full p-6 mb-6">
              <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M12 2v2"></path>
                <path d="M12 20v2"></path>
                <path d="m4.93 4.93 1.41 1.41"></path>
                <path d="m17.66 17.66 1.41 1.41"></path>
                <path d="M2 12h2"></path>
                <path d="M20 12h2"></path>
                <path d="m6.34 17.66-1.41 1.41"></path>
                <path d="m19.07 4.93-1.41 1.41"></path>
                <circle cx="12" cy="12" r="4"></circle>
              </svg>
            </div>
            <h2 className="text-2xl font-bold mb-2">Welcome to Weather AI</h2>
            <p className="text-center text-gray-600 mb-8">
              Get instant weather information for any location worldwide.<br />
              Ask about current conditions, forecasts, or weather patterns.
            </p>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 w-full max-w-2xl">
              {suggestions.map(suggestion => (
                <button
                  key={suggestion.id}
                  className="flex items-center p-4 border rounded-lg hover:bg-gray-50 text-left"
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  <div className="text-blue-500 mr-3">
                    <WeatherIcon type={suggestion.icon} />
                  </div>
                  <span>{suggestion.text}</span>
                </button>
              ))}
            </div>
          </div>
        ) : (
          messages.map((message, index) => (
            <MessageContainer key={index} isUser={message.isUser}>
              <Avatar isUser={message.isUser}>
                {message.isUser ? 'U' : 'AI'}
              </Avatar>
              <MessageBubble isUser={message.isUser}>
                {message.isLoading ? (
                  <div className="flex items-center">
                    <div className="animate-pulse mr-2">⏳</div>
                    {message.text}
                  </div>
                ) : message.weatherData ? (
                  <div>
                    <div>{message.text}</div>
                    {renderWeatherCard(message.weatherData)}
                  </div>
                ) : (
                  message.text
                )}
              </MessageBubble>
            </MessageContainer>
          ))
        )}
      </ChatContainer>
      
      <InputContainer>
        <Input
          type="text"
          placeholder="Ask about weather in any city..."
          value={input}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          disabled={loading}
        />
        <SendButton onClick={handleSendMessage} disabled={loading || input.trim() === ''}>
          <Send size={20} />
        </SendButton>
      </InputContainer>
      <div className="text-center text-xs text-gray-500 py-2">
        Press Enter to send • Shift+Enter for new line
      </div>
    </AppContainer>
  );
};

export default App;
